sqlReaderQuery.value:
@concat(
    'SELECT * FROM [', item().TableName,']',
    if(
        equals(item().ParquetLastLoadedValue, null),
        '',
        concat(
            ' WHERE ',
            item().IncrementalColumnName,
            if(
                equals(item().columnType, 'id'),
                concat(' > ', item().ParquetLastLoadedValue),
                concat(' > CAST(''', item().ParquetLastLoadedValue, ''' AS DATETIME)')
            )
        )
    )
)

@concat(
    'SELECT *,0 AS LoadFlag FROM [', item().TableName,']',
    if(
        equals(item().lastloadedvalue, null),
        '',
        concat(
            ' WHERE ',
            item().IncrementalColumnName,
            if(
                equals(item().columnType, 'id'),
                concat(' > ', item().lastloadedvalue),
                concat(' > CAST(''', item().lastloadedvalue, ''' AS DATETIME)')
            )
        )
    )
)

